Bash<pre><code># List containers
docker ps                    # Running containers
docker ps -a                # All containers

# Start/stop containers
docker start &lt;container&gt;
docker stop &lt;container&gt;
docker restart &lt;container&gt;

# Remove containers
docker rm &lt;container&gt;
docker rm -f &lt;container&gt;    # Force remove
docker container prune      # Remove all stopped

# Execute commands
docker exec -it &lt;container&gt; /bin/bash
docker exec &lt;container&gt; &lt;command&gt;

# View logs
docker logs &lt;container&gt;
docker logs -f &lt;container&gt;     # Follow logs
docker logs --tail 100 &lt;container&gt;
</code></pre>

Bash<pre><code># List networks
docker network ls

# Create network
docker network create &lt;name&gt;

# Connect container
docker network connect &lt;network&gt; &lt;container&gt;

# Inspect network
docker network inspect &lt;network&gt;
</code></pre>

Bash<pre><code># List volumes
docker volume ls

# Create volume
docker volume create &lt;name&gt;

# Remove volumes
docker volume rm &lt;name&gt;
docker volume prune         # Remove unused volumes

# Inspect volume
docker volume inspect &lt;name&gt;
</code></pre>

Bash<pre><code>docker run -e VAR=value &lt;image&gt;
docker run --env-file .env &lt;image&gt;
</code></pre>

Bash<pre><code>docker run -p 8080:80 &lt;image&gt;
docker run -p 127.0.0.1:8080:80 &lt;image&gt;
</code></pre>

Bash<pre><code>docker run -v /host/path:/container/path &lt;image&gt;
docker run -v volume_name:/container/path &lt;image&gt;
</code></pre>

Bash<pre><code># Inspect container
docker inspect &lt;container&gt;

# Check container processes
docker top &lt;container&gt;

# View container changes
docker diff &lt;container&gt;

# Export/import
docker export &lt;container&gt; &gt; container.tar
docker import container.tar &lt;image&gt;

# Save/load images
docker save &lt;image&gt; &gt; image.tar
docker load &lt; image.tar
</code></pre>

Bash<pre><code># Cluster info
kubectl cluster-info
kubectl version
kubectl api-resources

# Node management
kubectl get nodes
kubectl describe node &lt;node&gt;
kubectl top nodes
kubectl cordon &lt;node&gt;
kubectl uncordon &lt;node&gt;
kubectl drain &lt;node&gt;
</code></pre>

Bash<pre><code># List pods
kubectl get pods
kubectl get pods -n &lt;namespace&gt;
kubectl get pods -o wide
kubectl get pods --all-namespaces

# Describe pod
kubectl describe pod &lt;pod&gt;

# Pod logs
kubectl logs &lt;pod&gt;
kubectl logs -f &lt;pod&gt;
kubectl logs &lt;pod&gt; -c &lt;container&gt;
kubectl logs &lt;pod&gt; --previous

# Execute commands
kubectl exec -it &lt;pod&gt; -- /bin/bash
kubectl exec &lt;pod&gt; -- &lt;command&gt;

# Port forwarding
kubectl port-forward &lt;pod&gt; 8080:80
</code></pre>

Bash<pre><code># Create deployment
kubectl create deployment &lt;name&gt; --image=&lt;image&gt;

# List deployments
kubectl get deployments

# Scale deployment
kubectl scale deployment &lt;name&gt; --replicas=3

# Update image
kubectl set image deployment/&lt;name&gt; &lt;container&gt;=&lt;image&gt;

# Rollout management
kubectl rollout status deployment/&lt;name&gt;
kubectl rollout history deployment/&lt;name&gt;
kubectl rollout undo deployment/&lt;name&gt;
kubectl rollout restart deployment/&lt;name&gt;
</code></pre>

Bash<pre><code># List namespaces
kubectl get namespaces
kubectl get ns

# Create namespace
kubectl create namespace &lt;name&gt;

# Delete namespace
kubectl delete namespace &lt;name&gt;

# Set default namespace
kubectl config set-context --current --namespace=&lt;name&gt;
</code></pre>

Bash<pre><code># ConfigMaps
kubectl create configmap &lt;name&gt; --from-literal=key=value
kubectl create configmap &lt;name&gt; --from-file=&lt;file&gt;
kubectl get configmaps
kubectl describe configmap &lt;name&gt;

# Secrets
kubectl create secret generic &lt;name&gt; --from-literal=key=value
kubectl create secret generic &lt;name&gt; --from-file=&lt;file&gt;
kubectl get secrets
kubectl describe secret &lt;name&gt;
</code></pre>

Bash<pre><code># Apply manifests
kubectl apply -f &lt;file&gt;
kubectl apply -f &lt;directory&gt;
kubectl apply -k &lt;kustomize-dir&gt;

# Delete resources
kubectl delete -f &lt;file&gt;
kubectl delete pod &lt;pod&gt;
kubectl delete deployment &lt;deployment&gt;

# Get all resources
kubectl get all
kubectl get all -n &lt;namespace&gt;

# Label management
kubectl label pod &lt;pod&gt; env=prod
kubectl label pod &lt;pod&gt; env-

# Annotation management
kubectl annotate pod &lt;pod&gt; description="text"
</code></pre>

Bash<pre><code># View pod logs
kubectl logs &lt;pod-name&gt;
kubectl logs -f &lt;pod-name&gt;
kubectl logs &lt;pod-name&gt; --previous

# Execute commands in pod
kubectl exec -it &lt;pod-name&gt; -- /bin/sh

# Port forwarding
kubectl port-forward &lt;pod-name&gt; 8080:80

# Describe resources
kubectl describe pod &lt;pod-name&gt;
kubectl describe node &lt;node-name&gt;
</code></pre>

Bash<pre><code># Pod not starting
kubectl describe pod &lt;pod-name&gt;
kubectl logs &lt;pod-name&gt;

# Image pull errors
kubectl describe pod &lt;pod-name&gt; | grep -A 5 Events

# Resource constraints
kubectl top pod &lt;pod-name&gt;
</code></pre>
